l j = 0\n                                    local k = 1\n                                    local case = 0\n                                    while j < sp_time do\n                                        if case == 0 then\n                                            map[j] = sp\n                                            case = 1\n                                        else\n                                            map[-j] = sp\n                                            case = 0\n                                            k = k + 1\n                                        end\n                                    end\n                                end\n                                return map\n\n                            end\n\n                            local map = getamountmap(x1, y1, x2, y2)\n                            for i, drawtime in pairs(map) do\n                                local q = 0\n                                while q < drawtime do\n                                    set(x1, y1, value)\n                                    if xl == big then\n                                        y1 = y1 + 1\n                                    else\n                                        x1 = x1 + 1\n\n                                    end\n                                    if xl == big then\n                                        x1 = x1 + 1\n                                    else\n                                        y1 = y1 + 1\n                                    end\n                                    set(x1, y1, value)\n\n                                end\n                            end\n\n                        end\n                    end\n                end\n                if mode == \"square\" then\n                    local x1 = Aargs[1]\n                    local y1 = Aargs[2]\n                    local x2 = Aargs[3]\n                    local y2 = Aargs[4]\n                    local value = Aargs[5]\n          