  end\n            end\n\n\n            if tostring(name) ~= 'nil' then\n                local fd = Octopus.logic.strtotable(getVar(args, \"c\", \".\" .. folder))\n                ---@diagnostic disable-next-line: need-check-nil\n                fd[name] = value\n                setVar(args, \"c\", \".\" .. folder, Octopus.logic.tostring(fd))\n            else\n                setVar(args, \"c\", \".\" .. folder, Octopus.logic.tostring(value))\n            end\n\n            if hasregisteredfolder(folder) then\n                registerfolder(folder)\n            end\n\n\n\n\n\n        end,\n        ---@param folder string\n        ---@param name string\n        get_value = function(folder, name)\n            return Octopus.logic.strtotable(getVar(args, \"c\", \".\" .. folder))[name]\n        end,\n        ---@param folder string\n        get_folder = function(folder)\n\n            return Octopus.logic.strtotable(getVar(args, \"c\", \".\" .. folder))\n\n\n        end,\n        cleartemp = function()\n            for i, folder_name in pairs(Octopus.Data.get_folder(\"*folderlist\")) do\n                if string.find(folder_name, \"temp_\") ~= nil then\n                    Octopus.Data.save(folder_name, nil, nil)\n                end\n            end\n        end,\n        ---@param scriptname string\n        ---@param value any\n        ---@\n        send = function(scriptname, value)\n\n            if type(value) == \"number\" then\n                value = tostring(value)\n            elseif type(value) == \"table\" then\n                value = Octopus.logic.tostring(value)\n            end\n\n\n            effect(\"signal_send\", args, scriptname, value)\n        end,\n        eventargs_analysis = function()\n            local EventArgs = getVar(args, \"c\", \"EventArgs\")\n            local tmp = {}\n            local c = 0\n            while string.find(EventArgs, \"||\") ~= nil do\n                c = c + 1\n                local t = string.sub(EventArgs, 1, string.find(EventArgs, \"||\") - 1)\n                tmp[tostring(c)] = t\n                EventArgs = EventArgs:sub(EventArgs:find(\"||\") + 1, #EventArgs)\n\n            end\n            return tmp\n        end,\n    },\n    Sound = {\n\n        playSoundSelf = function(soundID)\n\n            effect(\"sound_id_self\", args, nil, soundID)\n\n        end\n\n    },\n    Listener = {\n        ---@diagnostic disable-next-line: undefined-doc-name\n        open = function(Aargs) ---@param Aargs listner listner:{type = ,scriptname = scriptname,id = id}\n            local type = Aargs[\"type\"]\n            local id = Aargs[\"id\"]\n            setVar(args, \"c\", type, \"TRUE\")\n            local R_Listner = Octopus.Data.get_folder(\"RegisteredListener\")\n            if R_Listner[type] ~= nil then\n                R_Listner[type][id] = { scriptname = Aargs[\"scriptname\"] }\n            else\n                R_Listner[type] = {}\n                R_Listner[type][id] = { scriptname = Aargs[\"scriptname\"] }\n            end\n            Octopus.Data.save(\"RegisteredListener\", nil, R_Listner)\n        end,\n        ---@diagnostic disable-next-line: undefined-doc-name\n        ---@param type string\n        ---@param id string\n        close = function(type, id)\n\n            local listners = Octopus.Listener.get()\n            if listners[type] ~= nil then\n                listners[type][id] = nil\n            end\n            if Octopus.logic.length(listners[type]) == 0 then\n                setVar(args, \"c\", type, \"FALSE\")\n            end\n            Octopus.Data.save(\"RegisteredListener\", nil, listners)\n        end,\n\n        ---@param type string the type of the listner (OnMessage_system/OnMessage_say/OnMessage_yell/OnMessage_emote/OnMessage_raid/OnMessage_party/OnPlayerStartMove/Always/OnTRP3_roll/OnTRP3_kill/OnTRP3_item_used)\n        ---@param scriptname string the script that the listner will execute\n        ---@param id string the id of the lisner,must be unique\n        new = function(type, scriptname, id)\n            id = id or \"DEFAULT\"\n            if type == \"OnMessage_say\" or\n                type == \"OnMessage_yell\" or\n                type == \"OnMessage_emote\" or\n                type == \"OnMessage_raid\" or\n                type == \"OnMessage_party\" or\n                type == \"OnPlayerStartMove\" or\n                type == \"Always\" or\n                type == \"OnTRP3_roll\" or\n                type == \"OnTRP3_kill\" or\n                type == \"OnTRP3_item_used\" or\n                type == \"OnMessage_system\"\n            then\n                return { type = type, scriptname = scriptname, id = id }\n            else\n                error(\"ERROR:Invaild Listener Type\")\n            end\n        end,\n        ---@return table listners\n        get = function()\n            return Octopus.Data.get_folder(\"RegisteredListener\")\n        end,\n        save = function(table)\n            Octopus.Data.save(\"RegisteredListener\", nil, table)\n        end\n\n\n\n    },\n\n    RunScript = function(scriptname, scripts) --运行指定脚本\n\n        for i, v in pairs(scripts) do\n\n            if type(v) == \"table\" then\n                Octopus.RunScript(scriptname, v)\n\n            else\n                if i == scriptname then\n                    scripts[scriptname]()\n                end\n            end\n\n        end\n\n    end,\n    BasicScripts = {\n        menuclose = function()\n\n            if Octopus.GUI.Menu.Config.Confirming() == \"TRUE\" then\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.CANCEL)\n\n                effect(\"text\", args, \"已取消！\", \"4\")\n\n                Octopus.GUI.Menu.Config.Confirming(\"FALSE\")\n\n                --                Octopus.GUI.Menu.openlatest()\n            end\n\n\n\n            if Octopus.GUI.Menu.Config.CloseAble() ~= \"FALSE\" then\n\n\n                Octopus.GUI.Menu.setlatest(getVar(args, \"c\", \"MenuText\"))\n\n                Octopus.GUI.Menu.Config.MenuItem_UseAble(\"TRUE\")\n\n\n            else\n\n                Octopus.GUI.Menu.show()\n\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.CANCEL)\n\n                effect(\"text\", args, \"此页面不能被关闭！\", \"4\")\n\n            end\n        end,\n        ListnerRelatives = {\n            ListenerHeard = function()\n                local listners = Octopus.Data.get_folder(\"RegisteredListener\")\n                local atype = ScriptArgs\n\n                for i, v in pairs(listners[atype]) do\n                    ScriptArgs = v[\"scriptargs\"]\n                    Octopus.RunScript(v[\"scriptname\"], SCRIPTS)\n                end\n            end\n        },\n\n\n        windup = function()\n            setVar(args, \"c\", \"ScriptName\", \"nil\")\n            setVar(args, \"c\", \"ScriptArgs\", \"nil\")\n            setVar(args, \"c\", \"ScriptFilter\", \"nil\")\n        end\n\n    }\n\n\n}\n\n---章鱼END\nRunScript = Octopus.RunScript\n\n\nSignal = {\n\n    send = function(tag, text)\n\n        effect(\"signal_send\", args, tag, text)\n\n    end,\n\n    analysis = function()\n\n\n        SignalValue = getVar(args, \"c\", \"SIGNAL_TEXT\")\n        local tag = getVar(args, \"c\", \"SIGNAL_TAG\")\n\n        RunScript(tag, SCRIPTS)\n\n    end\n\n}\n\n\nMenuLibrary = {\n    FIRSTTIME_LOADING = {\n    },\n\n    MAIN = \"{img:Interface\\\\QUESTFRAME\\\\UI-HorizontalBreak:256:64}\" ..\n        \"\\n{h2:c}>>>冒险大师<<<\\nAdventureMaster\\n{col:2a2a2a}KP界面{/col}{/h2}\\n\" ..\n        \"{img:Interface\\\\QUESTFRAME\\\\UI-HorizontalBreak:256:64}\\n\" ..\n        \"{h1:c}{link*playerlist.open*{icon:ability_paladin_beaconsoflight:16}}\" ..\n        \"{link*playerlist.open*玩家管理}{link*playerlist.open*{icon:ability_paladin_beaconsoflight:16}}\" ..\n        \"\\n\\n{link*statecontrol*{icon:petbattle_health:16}}{link*statecontrol*状态控制}{link*statecontrol*{icon:petbattle_health:16}}\\n\\n\"\n        ..\n        \"{link*dicemanage*{icon:inv_misc_dice_02:16}}{link*runner(scriptname=diceroller_openmenu)*判定界面}\" ..\n        \"{link*statecontrol*{icon:inv_misc_dice_02:16}}\\n\\n\" ..\n        \"{link*minigames*{icon:inv_prg_icon_puzzle14:16}}{link*runner(scriptname=author_word)*意见反馈}\" ..\n        \"{link*minigames*{icon:inv_prg_icon_puzzle14:16}}\\n{/h1}\"\n    ,\n    DiceRoller_Main = Octopus.Assests.PICTURES.HEADLINE ..\n        \"{h1:c}骰子判定{/h1}\\n\\n\\n\" ..\n        \"{h3:c}{link*runner(scriptname=diceroller_judge_menu)*{icon:ability_warrior_commandingshout:42}}{/h3}\" ..\n        \"{h2:c}{link*runner(scriptname=diceroller_judge_menu)*发起判定}{/h2}\" .. \"\\n\\n\\n\\n\\n\" ..\n        \"{h3:c}{link*runner(scriptname=diceroller_dicestream_menu)*{icon:inv_relics_hourglass:42}}{/h3}\" ..\n        \"{h2:c}{link*runner(scriptname=diceroller_dicestream_menu)*管理判定流}{/h2}\"\n        .. Octopus.Assests.PICTURES.HEADLINE\n        .. \"{h2:c}{link*runner(scriptname=tomain)*返回主界面}{/h2}\"\n    ,\n    DiceRoller_Dicestream_Main = function()\n        local text = Octopus.Assests.PICTURES.HEADLINE .. \"{h3:c}\"\n        local pages = {}\n        local dicestreams = Octopus.Data.get_folder(\"DiceStreamLibrary\")\n\n        --打印已有的骰子流\n        if Octopus.logic.length(dicestreams) > 0 then\n            local c = 0\n            for i, v in pairs(dicestreams) do\n\n                c = c + 1\n                local ds = dicestreams[tostring(c)]\n                text = text ..\n                    string.format(\"%s: %s(需要:%s,奖励%s,%sd%s) %s\\n\",\n                        tostring(c),\n                        ds[\"judge_type\"],\n                        ds[\"needed_point\"],\n                        ds[\"award_point\"],\n                        ds[\"dice_num\"],\n                        ds[\"dice_surface\"],\n                        string.format(\"{link*runner(scriptname=%s,scriptargs=%s)*更改}\",\n                            \"dicestream_set\",\n                            tostring(c)\n                        )\n                    )\n            end\n        else\n            text = text .. \"还没有判定流！\\n\"\n        end\n        text = text .. \"{link*runner(scriptname=dicestream_new)*+新建判定流+}\"\n        text = text .. \"{/h3}\" .. Octopus.Assests.PICTURES.HEADLINE\n            .. \"{h2:c}{link*runner(scriptname=diceroller_openmenu)*返回上一级}{/h2}\"\n        return text\n    end,\n    Diceroller_sender_main = function()\n\n        local target = Utils.PlayerList.getTargetInfo()\n        local player_name = \"\"\n        local player_class = \"\"\n        if target ~= 'notaplayer' then\n            player_name = target['name']\n            player_class = Octopus.Assests.Icons[target[\"class\"]]\n        else\n            player_class = \"\"\n            player_name = \"没有玩家\"\n        end\n        local text = Octopus.Assests.PICTURES.HEADLINE\n        local dicestream = {}\n        local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n        if Octopus.logic.length(sender_info) < 1 then\n            sender_info = {\n                dicestream_id = \"1\",\n                judge_mode = \"solo\", --team\n                sender_mode = \"online\", --offline\n                team_mode = \"allpass\" --sum\n            }\n            Octopus.Data.save(\"Diceroller_sender_info\", nil, sender_info)\n        end\n        dicestream = Octopus.Data.get_folder(\"DiceStreamLibrary\")[sender_info[\"dicestream_id\"]]\n\n        text = text .. \"{h1:c}判定准备{/h1}\"\n            .. \"{h3:c}你当前的目标：\" .. player_class .. player_name\n            ..\n            \"\\n选择的判定流：\" ..\n            sender_info[\"dicestream_id\"] .. \"{link*runner(scriptname=dicesender_change,scriptargs=dicestream_id)*选择}\"\n            .. string.format(\"\\n{col:e0e0e0}(%s,需要%s,奖励%s,%sd%s){/col}\\n\",\n                dicestream[\"judge_type\"],\n                dicestream[\"needed_point\"],\n                dicestream[\"award_point\"],\n                dicestream[\"dice_num\"],\n                dicestream[\"dice_surface\"])\n            .. \"判定模式（单人(1)/群体(2)）：\" .. LangTrans.SENDER_OPTION[sender_info[\"judge_mode\"]]\n            .. \"　{link*runner(scriptname=dicesender_change,scriptargs=judge_mode)*切换}\\n\"\n            .. \"{col:525252}单人：对一个玩家发送掷骰/群体：所有【已在玩家列表】的玩家都可掷骰（/roll）并统计{/col}\\n\"\n        if sender_info[\"judge_mode\"] == \"team\" then\n            text = text .. \"　　群体模式 全过(1)/总和(2)：\" ..\n                LangTrans[\"SENDER_OPTION\"][sender_info[\"team_mode\"]]\n                .. \"　{link*runner(scriptname=dicesender_change,scriptargs=team_mode)*切换}\"\n\n            text = text .. \"\\n{col:525252}全过：所有玩家都要通过（暂时只支持单骰判定）/总和：所有玩家总和超过即可{/col}\\n\"\n        end\n        if sender_info['judge_mode'] == 'solo' then\n            text = text .. \"发送模式（在线(1)/离线(2)）：\" ..\n                LangTrans.SENDER_OPTION[sender_info[\"sender_mode\"]]\n                .. \"　{link*runner(scriptname=dicesender_change,scriptargs=sender_mode)*切换}\\n\"\n                .. \"{col:525252}在线：双方必须互为目标，并且都开启了《冒险大师》剧本(对群体模式无效)/离线：对方无需打开《冒险大师》剧本，使用/roll进行roll点{/col}\"\n        end\n        text = text .. \"{/h3}\\n\\n\"\n            .. \"{h2:c}{link*runner(scriptname=diceroller_sender_send)*发送！}{/h2}\"\n\n\n\n        text = text .. Octopus.Assests.PICTURES.HEADLINE\n        text = text .. \"{h2:c}{link*runner(scriptname=diceroller_openmenu)*返回上一级}{/h2}\"\n        return text\n    end,\n    --单人等待中\n    Diceroller_sender_waiting1 = function()\n        local text = Octopus.Assests.PICTURES.HEADLINE\n        text = text .. (\"{h1:c}等待\" .. getVar(args, \"c\", \"sender_targetplayer\") .. \"的反馈\\n......{/h1}\")\n            .. \"\\n\\n{h3:c}{link*runner(scriptname=diceroller_judge_cancel)*点击取消}{/h3}\"\n            .. Octopus.Assests.PICTURES.HEADLINE\n        return text\n\n    end,\n    --群体等待中\n    Diceroller_sender_waiting2 = function()\n        --[[\n            temp_results = {[1] = {...}}\n            --]]\n        local text = Octopus.Assests.PICTURES.HEADLINE\n        local tmps = Octopus.Data.get_folder(\"temp_results\") --results\n        local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n        text = text .. (\"{h1:c}得到的反馈信息（%s）{/h1}\"):format(LangTrans.SENDER_OPTION[\n            sender_info['team_mode']])\n            .. (\"{h3:c}{col:606060}(共 %s 条){/col}{/h3}\"):format(\n                tostring(Octopus.logic.length(tmps))\n            )\n\n        if sender_info['team_mode'] == \"allpass\" then\n            text = text .. \"{h2:c}\"\n            local i = 0\n            while i < Octopus.logic.length(tmps) do\n                i = i + 1\n                local result = tmps[tostring(i)]\n                text = text\n                    .. (\"%s：玩家：%s||点数：%s||最终点数：%s||判定结果：%s\\n\"):format(\n                        tostring(i),\n                        result['1'],\n                        result['2'],\n                        result['4'],\n                        LangTrans.JUDGE_RESULT[result['5']]\n\n                    )\n\n            end\n\n        elseif sender_info['team_mode'] == \"sum\" then\n            local sumpoint = 0\n            local i = 0\n            while i < Octopus.logic.length(tmps) do\n                i = i + 1\n                local result = tmps[tostring(i)]\n                sumpoint = sumpoint + tonumber(result[\"4\"])\n\n            end\n\n            text = text .. \"{h2:c}\"\n            text = text .. (\"{col:0c98dc}>总计点数：%s<{/col}\\n\\n\"):format(\n                tostring(sumpoint)\n            )\n            local i = 0\n            while i < Octopus.logic.length(tmps) do\n                i = i + 1\n                local result = tmps[tostring(i)]\n                text = text\n                    .. (\"%s：玩家：%s||点数：%s||最终点数：%s\\n\"):format(\n                        tostring(i),\n                        result['1'],\n                        result['2'],\n                        result['4']\n                    )\n            end\n        end\n        text = text .. \"{/h2}\"\n            .. \"{h3:c}{link*runner(scriptname=dice_feedback_team_end)*结束并统计}{/h3}\"\n\n        text = text .. Octopus.Assests.PICTURES.HEADLINE\n        return text\n\n    end,\n    ---@param Aargs table {[1]:countplayer,[2]:sumresult,[3]:winner,[4]failure,[5]:drawer,[6]:sumpoint,[7]sorted_result}\n    DiceRoller_sender_team_end = function(Aargs)\n        Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.FLAME)\n\n        local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n        local dicestream = Octopus.Data.get_folder(\"DiceStreamLibrary\")[sender_info['dicestream_id']]\n\n        local text = Octopus.Assests.PICTURES.HEADLINE\n            .. \"{h1:c}群体判定结果{/h1}\"\n            .. (\"{h3:c}共收到反馈 %s 份{/h3}\"):format(tostring(Aargs[1]))\n\n        if sender_info['team_mode'] == 'allpass' then\n            text = text .. (\"{h2:c}{col:23d50f}>通过判定的玩家<{/col}{/h2}\")\n                .. \"{h3:c}\"\n            for i, v in pairs(Aargs[3]) do\n                text = text .. v .. \"\\n\"\n            end\n            text = text .. \"{/h3}\"\n            text = text .. (\"{h2:c}{col:ca0000}>未通过判定的玩家<{/col}{/h2}\")\n                .. \"{h3:c}\"\n            for i, v in pairs(Aargs[4]) do\n                text = text .. v .. \"\\n\"\n            end\n            text = text .. \"{/h3}\"\n            text = text .. (\"{h2:c}{col:187e94}>判定平局的玩家<{/col}{/h2}\")\n                .. \"{h3:c}\"\n            for i, v in pairs(Aargs[5]) do\n                text = text .. v .. \"\\n\"\n            end\n            text = text .. \"{/h3}\"\n        elseif sender_info['team_mode'] == 'sum' then\n            text = text .. (\"{h2:c}{col:23d50f}>总计点数<{/col}{/h2}\")\n                .. (\"{h1:c}%s{/h1}\"):format(tostring(Aargs[6]))\n                .. (\"{h2:c}{col:23d50f}>结果<{/col}{/h2}\")\n                .. (\"{h1:c}%s{/h1}\"):format(LangTrans.JUDGE_RESULT[tostring(Aargs[2])])\n        end\n        text = text .. (\"{h2:c}>点数排行<{/h2}{h3:c}\")\n        local i = 0\n        while i < Octopus.logic.length(Aargs[7]) do\n            i = i + 1\n            local v = Aargs[7][tostring(i)]\n            text = text .. (\"%s：%s（%s(+%s)）\\n\"):format(\n                tostring(i),\n                v['1'],\n                tostring(v['2']),\n                dicestream['award_point']\n            )\n        end\n        text = text .. \"{/h3}\"\n\n\n        text = text .. Octopus.Assests.PICTURES.HEADLINE\n\n        text = text .. \"{h2:c}{link*runner(scriptname=diceroller_judge_menu)*返回上一级}{/h2}\"\n        text = text .. \"{h2:c}{link*runner(scriptname=tomain)*返回主界面}{/h2}\"\n        return text\n    end,\n    Diceroller_judgeGUI = function()\n        local judge_info = Octopus.Data.get_folder(\"Judging_Info\")\n        if Octopus.logic.length(judge_info) > 0 then\n            local text = Octopus.Assests.PICTURES.HEADLINE\n            text = text .. \"{h1:c}！你正在进行判定！{/h1}{h2:c}\"\n                .. (\"来自：%s\\n\"):format(judge_info[\"sender\"])\n                .. (\"判定内容：%s\\n\"):format(judge_info[\"judge_type\"])\n                .. (\"需要点数：%s\\n\"):format(judge_info[\"needed_point\"])\n                .. (\"额外奖励点：%s\\n\"):format(judge_info[\"award_point\"])\n                .. (\"骰子面数：%s\\n\"):format(judge_info[\"dice_surface\"])\n                .. (\"骰子数量：%s\\n\"):format(judge_info[\"dice_num\"])\n                .. \"{/h2}\\n\"\n                .. (\"{h3:c}{icon:inv_misc_dice_01:25}{link*%s*>>点击掷出骰子<<}{icon:inv_misc_dice_01:25}\"):format(\n                    \"runner(scriptname=diceroller_roll)\")\n                .. (\"\\n{col:df0000}{link*runner(scriptname=diceroller_roll_cancel)*点击取消}{/col}{/h3}\")\n                .. Octopus.Assests.PICTURES.HEADLINE\n            return text\n        else\n            Octopus.LOG.error(\"没有正在进行的判定。\")\n        end\n    end,\n    Diceroller_judgeGUI_final = function(judge_result, identy)\n        local text = Octopus.Assests.PICTURES.HEADLINE\n        Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.FLAME)\n        identy = identy or \"sender\"\n        text = text .. \"{h1:c}判定完成{/h1}\"\n            .. \"{h2:c}\"\n            .. (\"得到的点数：%s\\n\"):format(judge_result[\"point\"])\n            .. (\"加成后点数：%s\\n\"):format(judge_result[\"total_point\"])\n            .. (\"判定结果：%s\"):format(LangTrans.JUDGE_RESULT[judge_result['result']])\n            .. \"{/h2}\"\n\n\n            .. Octopus.Assests.PICTURES.HEADLINE\n        if identy == \"receiver\" then\n            text = text .. \"{h2:c}{link*runner(scriptname=tomain)*返回主界面}{/h2}\"\n        else\n\n            text = text .. \"{h2:c}{link*runner(scriptname=diceroller_judge_menu)*返回上一级}{/h2}\"\n            text = text .. \"{h2:c}{link*runner(scriptname=tomain)*返回主界面}{/h2}\"\n        end\n        return text\n    end,\n    Helper = function()\n        local pic_endofstorm = \"{icon:inv_stormswake:128}\"\n        local text           = Octopus.Assests.PICTURES.HEADLINE\n            .. \"{h3:c}\"\n            .. pic_endofstorm .. \"{/h3}\"\n            .. \"{h2:c}意见反馈邮箱{/h2}\"\n            .. \"{h2:c}1120370331@qq.com\\n\\n{/h2}\"\n            .. \"\\n{h3:c}{col:4c4c4c}作者的牢骚\"\n            .. \"\\n虽然这个小剧本的功能目前还挺邵少的，但是在开发这个物品的过程中，\"\n            .. \"我同时写了很多的开发框架，所以实际开发时间还挺长的……已经有三千多行代码了(｀·ω·‘)\"\n            .. \"做这个的目的是为了让一些活动能更有特色吧。也是为了让KP在指挥扔骰子的时候不那么忙了。\"\n            .. \"随着国服的关服，这个物品又该何去何从呢~\"\n            .. \"其实我还有一些其他的想法，比如加一个可以内置在这里面的任务系统，让所有人都可以一键发布任务，但还是，过段时间再说吧~无论如何，请您享用这个小玩意ヽ(·ω·){/h3}\"\n            .. Octopus.Assests.PICTURES.HEADLINE\n            .. \"{h2:c}{link*runner(scriptname=tomain)*返回主界面}{/h2}\"\n        return text\n    end\n\n\n\n}\n\n\nLangTrans = {\n\n    STATES = {\n        extended = {\n            health = \"{icon:petbattle_health:16}{col:ae0000}生命{/col}{icon:petbattle_health:16}\",\n            coins = \"{icon:inv_misc_coin_02:16}{col:f4e200}金币{/col}{icon:inv_misc_coin_02:16}\",\n            foucs = \"{icon:petbattle_speed:16}{col:008080}精力{/col