open(MenuLibrary.DiceRoller_Main)\n        end,\n        diceroller_dicestream_menu = function()\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.DIG)\n            Octopus.GUI.Menu.open(MenuLibrary.DiceRoller_Dicestream_Main())\n        end,\n        dicestream_set = function()\n            local dicestream = Utils.DiceRoller.DiceStream.getLibrary()[ScriptArgs]\n            setVar(args, \"c\", \"dicestream_editingID\", ScriptArgs)\n            Octopus.Data.save(\"temp_editingdicestream\", nil, dicestream)\n            local text = Octopus.Assests.PICTURES.HEADLINE ..\n                \"{h1:c}判定流配置{/h1}{h3:c}\" ..\n                string.format(\n                    \"判定类型：%s %s\\n\",\n                    dicestream[\"judge_type\"],\n                    \"{link*input(scriptname=dicestream_setsave1,scriptargs=judge_type)*更改}\") ..\n                string.format(\n                    \"需要点数：%s %s\\n\",\n                    dicestream[\"needed_point\"],\n                    \"{link*input(scriptname=dicestream_setsave1,scriptargs=needed_point)*更改}\") ..\n                string.format(\n                    \"奖励点数：%s %s\\n\",\n                    dicestream[\"award_point\"],\n                    \"{link*input(scriptname=dicestream_setsave1,scriptargs=award_point)*更改}\") ..\n                string.format(\n                    \"骰子面数：%s %s\\n\",\n                    dicestream[\"dice_surface\"],\n                    \"{link*input(scriptname=dicestream_setsave1,scriptargs=dice_surface)*更改}\") ..\n                string.format(\n                    \"骰子数量：%s %s\\n\",\n                    dicestream[\"dice_num\"],\n                    \"{link*input(scriptname=dicestream_setsave1,scriptargs=dice_num)*更改}\") ..\n                \"\\n\" ..\n                \"{col:b30000}{link*runner(scriptname=dicestream_delete,scriptargs=\" ..\n                ScriptArgs .. \")*删除}{/col}{/h3}\" ..\n\n                Octopus.Assests.PICTURES.HEADLINE\n                .. \"{h2:c}{link*runner(scriptname=diceroller_dicestream_menu)*返回上一级}{/h2}\"\n            Octopus.GUI.Menu.open(text)\n\n        end,\n        dicestream_setsave1 = function()\n            local type = ScriptArgs\n            local value = getVar(args, \"c\", \"InputValue\")\n            local dicestream = Octopus.Data.get_folder(\"temp_editingdicestream\")\n            local function valid()\n                if type == \"needed_point\" or\n                    type == \"award_point\" or\n                    type == \"dice_num\" or\n                    type == \"dice_surface\"\n                then\n                    if (tonumber(value) ~= nil) and tonumber(value) % 1 == 0 then\n                        if type == \"dice_num\" or type == \"dice_surface\" then\n                            if tonumber(value) > 0 then\n                                return true\n                            end\n                        else\n                            return true\n                        end\n                    else\n                        return false\n                    end\n                else\n                    return true\n                end\n            end\n\n            if valid() == true then\n                dicestream[type] = value\n                Octopus.Data.save(\"DiceStreamLibrary\",\n                    getVar(args, \"c\", \"dicestream_editingID\"),\n                    dicestream)\n\n            else\n                Octopus.LOG.error(\"输入值不合法\")\n            end\n            ScriptArgs = getVar(args, \"c\", \"dicestream_editingID\")\n            RunScript(\"dicestream_set\", SCRIPTS)\n\n        end,\n        dicestream_new = function()\n            if Octopus.logic.length(Octopus.Data.get_folder(\"DiceStreamLibrary\")) < 50 then\n                Octopus.Data.save(\"DiceStreamLibrary\",\n                    Octopus.logic.length(Octopus.Data.get_folder(\"DiceStreamLibrary\")) + 1,\n                    Utils.DiceRoller.DiceStream.template)\n\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.SUCCESS)\n            else\n                Octopus.LOG.error(\"判定流上限为50个！\")\n            end\n            Octopus.GUI.Menu.open(MenuLibrary.DiceRoller_Dicestream_Main())\n        end,\n        dicestream_delete = function()\n            Octopus.Data.save(\"DiceStreamLibrary\",\n                ScriptArgs, nil\n            )\n            Octopus.Data.save(\n                \"DiceStreamLibrary\"\n                , nil, Octopus.logic.sortkeybynumber(Octopus.Data.get_folder(\"DiceStreamLibrary\"))\n            )\n            Octopus.GUI.Menu.open(MenuLibrary.DiceRoller_Dicestream_Main())\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.SUCCESS)\n            effect(\"text\", args, \"已为您删除此判定流！\", \"3\")\n        end,\n        dicesender_change = function()\n            local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n            if ScriptArgs == \"judge_mode\" or\n                ScriptArgs == \"sender_mode\" or\n                ScriptArgs == \"team_mode\" then\n                if ScriptArgs == \"judge_mode\" then\n                    if sender_info[ScriptArgs] == \"solo\" then\n                        sender_info[ScriptArgs] = \"team\"\n                    else\n                        sender_info[ScriptArgs] = \"solo\"\n                    end\n                end\n                if ScriptArgs == \"sender_mode\" then\n                    if sender_info[ScriptArgs] == \"online\" then\n                        sender_info[ScriptArgs] = \"offline\"\n                    else\n                        sender_info[ScriptArgs] = \"online\"\n                    end\n                end\n                if ScriptArgs == \"team_mode\" then\n                    if sender_info[ScriptArgs] == \"allpass\" then\n                        sender_info[ScriptArgs] = \"sum\"\n                    else\n                        sender_info[ScriptArgs] = \"allpass\"\n           