s.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.DIG)\n                else\n                    Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n                    Octopus.LOG.error(\"你必须先创建一个判定流才能这么做。已为您跳转！\")\n                    Octopus.RunScript(\"diceroller_dicestream_menu\", SCRIPTS)\n                end\n            else\n                Octopus.GUI.Menu.open(MenuLibrary.Diceroller_sender_waiting2())\n            end\n        end,\n        diceroller_sender_send = function()\n            local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.MACHINE)\n            local judge_info = Octopus.Data.get_folder(\"DiceStreamLibrary\")[sender_info[\"dicestream_id\"]]\n            if sender_info[\"judge_mode\"] == \"solo\" then\n                if op(\"unit_is_player\", args, \"target\") == true then\n                    if sender_info['sender_mode'] == \"online\" then\n                        if op(\"unit_name\", args, \"target\") ~= op(\"unit_name\", args, \"player\") then\n                            judge_info[\"sender\"] = op(\"unit_name\", args, \"player\")\n                            Octopus.Data.send(\"diceroller_received\", judge_info)\n                            local targetplayer = op(\"unit_name\", args, \"target\")\n                            setVar(args, \"c\", \"sender_targetplayer\", targetplayer)\n                            Octopus.GUI.Menu.open(MenuLibrary.Diceroller_sender_waiting1())\n                            effect(\"text\", args, \"已发送判定到\" .. op(\"unit_name\", args, \"target\"), \"3\")\n\n                        else\n                            Octopus.LOG.error(\"你不能发送判定到自己！\")\n                            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n                        end\n                    elseif sender_info['sender_mode'] == \"offline\" then\n                        local l = Octopus.Listener.new(\"OnMessage_system\", \"dice_feedback_solo_offline\",\n                            \"WaitingDiceFeedback\")\n                        Octopus.Listener.open(l)\n                        local targetplayer = op(\"unit_name\", args, \"target\")\n                        setVar(args, \"c\", \"sender_targetplayer\", targetplayer)\n                        setVar(args, \"c\", \"sender_dicenum\", judge_info[\"dice_num\"])\n\n                        Octopus.GUI.Menu.open(MenuLibrary.Diceroller_sender_waiting1())\n                        effect(\"speech_player\", args, \"/e\", (\"【OOC】要求%s进行判定！（请输入'/roll %s'，可输入%s次）\"\n                            ):format(\n                                targetplayer,\n                                judge_info[\"dice_surface\"],\n                                judge_info[\"dice_num\"]\n                            ))\n                    end\n                else\n                    Octopus.LOG.error(\"你必须先选中一个玩家！\")\n                    Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n                end\n\n            elseif sender_info[\"judge_mode\"] == \"team\" then\n                Octopus.Data.save(\"temp_results\", nil, {})\n                Octopus.Data.save(\"temp_results_final\", nil, {})\n                if sender_info[\"team_mode\"] == \"allpass\" then\n                    local l = Octopus.Listener.new(\"OnMessage_system\", \"dice_feedback_team\",\n                        \"WaitingDiceFeedback\")\n                    Octopus.Listener.open(l)\n                    Octopus.GUI.Menu.open(MenuLibrary.Diceroller_sender_waiting2())\n                    effect(\"speech_player\", args, \"/e\", (\"正在进行群体判定（全体通过模式）！请需要参与的玩家输入‘/roll %s’掷骰！\"\n                        ):format(\n                            judge_info[\"dice_surface\"]\n                        ))\n                    effect(\"text\", args, \"c\", \"开始群体判定！\")\n                    setVar(args, \"c\", \"TeamJudging\", \"TRUE\")\n\n\n                elseif sender_info[\"team_mode\"] == \"sum\" then\n                    local l = Octopus.Listener.new(\"OnMessage_system\", \"dice_feedback_team\",\n                        \"WaitingDiceFeedback\")\n                    Octopus.Listener.open(l)\n                    Octopus.GUI.Menu.open(MenuLibrary.Diceroller_sender_waiting2())\n                    effect(\"speech_player\", args, \"/e\", (\"正在进行群体判定（总和模式）！请需要参与的玩家输入‘/roll %s’掷骰！\"\n                        ):format(\n                            judge_info[\"dice_surface\"]\n                        ))\n                    setVar(args, \"c\", \"TeamJudging\", \"TRUE\")\n                    effect(\"text\", args, \"c\", \"开始群体判定！\")\n                end\n\n            end\n\n\n        end,\n        diceroller_received = function()\n            if (Octopus.logic.length(Octopus.Data.get_folder(\"Judging_Info\")) > 0) == false then\n                local judge_info = Octopus.logic.strtotable(ScriptArgs)\n                effect(\"speech_player\", args, \"/e\", (\"【OOC】正在进行判定……\"))\n                Octopus.Data.save(\"Judging_Info\", nil, judge_info)\n                Octopus.GUI.Menu.open(MenuLibrary.Diceroller_judgeGUI())\n            else\n                effect(\"speech_player\", args, \"/e\", (\"【OOC】拒绝判定（已经有一个判定正在进行）\"))\n            end\n\n\n\n        end,\n        diceroller_judge_cancel = function()\n\n            local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n            local close_sys_listner = function()\n                Octopus.Listener.close(\"OnMessage_system\", \"WaitingDiceFeedback\")\n            end\n            if sender_info[\"judge_mode\"] == 'solo' then\n\n                if sender_info[\"sender_mode\"] == 'offline' then\n                    close_sys_listner()\n                    setVar(args, \"c\", \"sender_dicenum\", nil)\n                elseif sender_info[\"sender_mode\"] == 'online' then\n                    if op(\"unit_name\", args, \"target\") == getVar(args, \"c\", \"sender_targetplayer\") then\n                        Octopus.Data.send(\"diceroller_roll_cancel\", \"1\")\n                    else\n                        Octopus.LOG.error(\"你必须先选中被你指定过的玩家！\")\n                    end\n\n                end\n                setVar(args, \"c\", \"sender_targetplayer\", nil)\n            elseif sender_info['judge_mode'] == 'team' then\n                close_sys_listner()\n            end\n            effect(\"speech_player\", args, \"/e\", \"取消了判定\")\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n            effect(\"text\", args, \"已取消\", \"3\")\n            Octopus.GUI.Menu.open(MenuLibrary.DiceRoller_Main)\n        end,\n        dice_feedback_solo = function()\n            if ScriptArgs ~= \"cancel\" then\n                local judge_result = Octopus.logic.strtotable(ScriptArgs)\n                Octopus.GUI.Menu.open(MenuLibrary.Diceroller_judgeGUI_final(judge_result, 'sender'))\n            else\n                effect(\"text\", args, \"判定被取消了。\", \"3\")\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.CANCEL)\n                Octopus.GUI.Menu.open(MenuLibrary.DiceRoller_Main())\n            end\n\n\n        end,\n        dice_feedback_solo_offline = function()\n            local EventValue = Octopus.Data.eventargs_analysis()\n\n            if EventValue['1'] ~= nil and EventValue['1']:find(\"掷\") ~= nil then\n\n                local info = Utils.DiceRoller.DiceSender.systemmessage_process(EventValue[\"1\"])\n\n                if info[1] == getVar(args, \"c\", \"sender_targetplayer\") then\n\n                    local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n                    local dicestream = Octopus.Data.get_folder(\"DiceStreamLibrary\")[sender_info['dicestream_id']]\n                    if tonumber(getVar(args, \"c\", \"sender_point\")) == nil then\n                        setVar(args, \"c\", \"sender_point\", \"0\")\n                    end\n                    if tostring(info[3]) == dicestream['dice_surface'] then\n                        local dicenum = tonumber(getVar(args, \"c\", \"sender_dicenum\"))\n                        if dicenum == 1 then\n                            local judge_result = {}\n                            local getted_point = tonumber(info[2]) + tonumber(getVar(args, \"c\", \"sender_point\"))\n                            local result = \"FALSE\"\n                            local total_point = getted_point + tonumber(dicestream['award_point'])\n                            if total_point > tonumber(dicestream['needed_point']) then\n                                result = \"TRUE\"\n                            elseif total_point == tonumber(dicestream['needed_point']) then\n                                result = \"DRAW\"\n                            end\n                            judge_result['result'] = result\n                            judge_result['total_point'] = total_point\n                            judge_result['point'] = getted_point\n                            Octopus.Listener.close(\"OnMessage_system\", \"WaitingDiceFeedback\")\n                            Octopus.GUI.Menu.open(MenuLibrary.Diceroller_judgeGUI_final(judge_result, 'sender'))\n                        else\n\n                            local getted_point = tonumber(info[2])\n                            local point = tonumber(getVar(args, \"c\", \"sender_point\"))\n                            dicenum = dicenum - 1\n                            point = point + getted_point\n                            setVar(args, \"c\", \"sender_point\", tostring(point))\n                            setVar(args, \"c\", \"sender_dicenum\", tostring(dicenum))\n                            effect(\"speech_player\", args, \"/e\", (\"【OOC】收到一次反馈，还剩%s次\"):format(\n                                tostring(dicenum)\n                            ))\n                        end\n                    end\n                end\n            end\n        end,\n        dice_feedback_team = function()\n            local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n            local EventValue = Octopus.Data.eventargs_analysis()\n            local results = Octopus.Data.get_folder(\"temp_results\")\n\n            if EventValue['1'] ~= nil and EventValue['1']:find(\"掷\") ~= nil then\n\n                local dicestream = Octopus.Data.get_folder(\"DiceStreamLibrary\")[sender_info['dicestream_id']]\n                local info = Utils.DiceRoller.DiceSender.systemmessage_process(EventValue['1'])\n                if tostring(info[3]) == dicestream['dice_surface'] then\n\n                    effect(\"text\", args, \"收到了来自 \" .. info[1] .. \" 的结果\")\n                    info[4] = tonumber(info[2]) + tonumber(dicestream['award_point'])\n                    local win = \"FALSE\"\n                    if info[4] > tonumber(dicestream['needed_point']) then\n                        win = \"TRUE\"\n                    elseif info[4] == tonumber(dicestream['needed_point']) then\n                        win = \"DRAW\"\n                    end\n                    info[5] = win\n                    results = Octopus.logic.append(results, info)\n                    Octopus.Data.save(\"temp_results\", nil, results)\n                    Octopus.GUI.Menu.open(MenuLibrary.Diceroller_sender_waiting2())\n\n                end\n            end\n\n        end,\n        dice_feedback_team_end = function()\n            local results = Octopus.Data.get_folder(\"temp_results\")\n            local sender_info = Octopus.Data.get_folder(\"Diceroller_sender_info\")\n            local dicestream = Octopus.Data.get_folder(\"DiceStreamLibrary\")[sender_info['dicestream_id']]\n\n            local sumpoint = 0\n            local sumresult = \"FALSE\"\n            local countplayer = 0\n            local winner = {}\n            local failure = {}\n            local drawer = {}\n            local ranked = {}\n            for i, v in pairs(results) do\n                sumpoint = sumpoint + tonumber(v['4'])\n                countplayer = countplayer + 1\n                if v[\"5\"] == \"TRUE\" then\n                    winner = Octopus.logic.append(winner, v['1'])\n                elseif v['5'] == \"FALSE\" then\n                    failure = Octopus.logic.append(failure, v['1'])\n                elseif v[\"5\"] == \"DRAW\" then\n                    drawer = Octopus.logic.append(drawer, v['1'])\n                end\n\n            end\n            if tonumber(dicestream['needed_point']) < sumpoint then\n                sumresult = \"TRUE\"\n            elseif tonumber(dicestream['needed_point']) == sumpoint then\n                sumpoint = \"DRAW\"\n            end\n            local temp = results\n            local i = 1\n            local n = 1\n            local o = 0\n            local res = {}\n            local bef = {}\n            while i < Octopus.logic.length(temp) do\n                i = i + 1 --the start location of res\n                o = i --the present location of res\n                res = temp[tostring(o)]\n\n                while (o > 1) do --if location > 1\n                    bef = temp[tostring(o - 1)] --get the value before it\n                    if tonumber(res['2']) > tonumber(bef['2']) then\n                        o = o - 1\n                        temp[tostring(o)], temp[tostring(o + 1)] = res, bef --exchange it\n                    else\n                        break\n                    end\n                end\n            end\n            local sorted_result = temp\n\n\n            Octopus.Data.save(\n                \"temp_results_final\",\n                nil,\n                {\n                    results = results,\n                    sumresult = sumresult,\n                    sumpoint = sumpoint,\n                    winner = winner,\n                    failure = failure,\n                    drawer = drawer,\n                    sorted_result = sorted_result\n\n\n                }\n            )\n            setVar(args, \"c\", \"TeamJudging\", \"FALSE\")\n            Octopus.Listener.close(\"OnMessage_system\", \"WaitingDiceFeedback\")\n            Octopus.GUI.Menu.open(MenuLibrary.DiceRoller_sender_team_end({ countplayer, sumresult, winner, failure,\n                drawer, sumpoint, sorted_result }))\n\n        end,\n        diceroller_roll = function()\n            local judge_info = Octopus.Data.get_folder(\"Judging_Info\")\n            if op(\"unit_name\", args, \"target\") == judge_info[\"sender\"] then\n                local dice_surface = judge_info[\"dice_surface\"]\n                local dice_num = tonumber(judge_info[\"dice_num\"])\n                local judge_result = {}\n                judge_result[\"result\"] = \"FALSE\"\n                local function rolladice(sf)\n                    local point = math.random(sf)\n                    return point\n                end\n\n                local total_point = 0\n                while dice_num > 0 do\n                    dice_num = dice_num - 1\n                    total_point = total_point + rolladice(dice_surface)\n                end\n                total_point = total_point + tonumber(judge_info[\"award_point\"])\n                if total_point > tonumber(judge_info[\"needed_point\"]) then\n                    judge_result['result'] = \"TRUE\"\n                elseif total_point == tonumber(judge_info['needed_point']) then\n                    judge_result['result'] = \"DRAW\"\n                end\n                judge_result['point'] = total_point - tonumber(judge_info[\"award_point\"])\n                judge_result['total_point'] = total_point\n                Octopus.Data.send(\"dice_feedback_solo\", judge_result)\n                Octopus.GUI.Menu.open(MenuLibrary.Diceroller_judgeGUI_final(judge_result, 'receiver'))\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.DICE)\n                effect(\"speech_player\", args, \"/e\", (\"【OOC】的判定%s！（需要点数：%s，最终点数%s）\"):\n                    format(\n                        LangTrans.JUDGE_RESULT.DEFAULT[judge_result['result']],\n                        judge_info['needed_point'],\n                        tostring(judge_result['total_point'])\n                    ))\n                Octopus.Data.save(\"Judging_Info\", nil, {})\n            else\n                Octopus.LOG.error(\"你必须先选中你的发送者为目标！\")\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n\n            end\n        end,\n        diceroller_roll_cancel = function()\n            if op(\"unit_name\", args, \"target\") == judge_info[\"sender\"] then\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n                effect(\"text\", args, \"已取消\", \"3\")\n                Octopus.Data.send(\"dice_feedback_solo\", \"cancel\")\n                Octopus.Data.save(\"Judging_Info\", nil, {})\n            else\n                Octopus.LOG.error(\"你必须先选中你的发送者为目标！\")\n                Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.ERROR)\n\n            end\n        end,\n        author_word = function()\n            Octopus.GUI.Menu.open(MenuLibrary.Helper())\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.JIHESHI)\n        end\n\n\n\n\n    },\n\n\n\n    onuse = function()\n        local ClickTime = tonumber(getVar(args, \"c\", \"MenuItemClickTime\"))\n        if Octopus.GUI.Menu.Config.MenuItem_UseAble() ~= \"FALSE\" or ClickTime >= 5 then\n            Octopus.GUI.Menu.Config.MenuItem_UseAble(\"FALSE\")\n            Octopus.GUI.Menu.openlatest()\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.MACHINE)\n            setVar(args, \"c\", \"MenuItemClickTime\", 0)\n        elseif ClickTime < 5 then\n            Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.CANCEL)\n            effect(\"text\", args, \"请勿重复打开菜单（连续点击5次可以强制打开）\", \"4\")\n            setVar(args, \"c\", \"MenuItemClickTime\", ClickTime + 1)\n\n        end\n    end,\n\n    init = function()\n\n        Octopus.GUI.Menu.setlatest(MenuLibrary.MAIN)\n        Octopus.Sound.playSoundSelf(Octopus.Assests.SoundLibrary.UI.MACHINE)\n        setVar(args, \"c\", \"MenuItemClickTime\", 0)\n        Octopus.GUI.Menu.openlatest()\n        setVar(args, \"c\", \"temp_input\", \"cancel\")\n        effect(\"run_workflow\", args, \"c\", \"listener.Always\")\n\n    end,\n\n\n\n\n\n\n\n}\n\n\n\n\n\n\n\n\nScriptName = getVar(args, \"c\", \"ScriptName\")\nScriptArgs = getVar(args, \"c\", \"ScriptArgs\")\nScriptFilter = getVar(args, \"c\", \"ScriptFilter\")\n---将被执行的代码\n\nOctopus.RunScript(ScriptName, Octopus.BasicScripts)\nOctopus.RunScript(ScriptName, SCRIPTS)\n\nif winditup ~= \"FALSE\" then\n    Octopus.windup() --clear some variables about scripts\nend",
},
