"if args._G.gnomeCanvas then\n    function clearFrame(frame)\n        frame:UnregisterAllEvents()\n        \n        local scriptTypes = {\"OnUpdate\", \"OnMouseDown\", \"OnMouseUp\"}\n        if frame:IsObjectType(\"Button\") then\n            table.insert(scriptTypes, \"OnClick\")\n        end\n        if frame:IsObjectType(\"Slider\") then\n            table.insert(scriptTypes, \"OnValueChanged\")\n        end\n        for _, script in ipairs(scriptTypes) do\n            frame:SetScript(script, nil)\n        end\n\n        local children = {frame:GetChildren()}\n        if #children > 0  then\n            for i = 1, #children do\n            clearFrame(children[i]) \n            end\n        end\n\n        local regions = {frame:GetRegions()}\n        for i = 1, #regions do\n            regions[i]:Hide()\n            regions[i]:SetParent(nil)\n        end\n\n        frame:SetParent(nil)\n\n        frame:ClearAllPoints()\n        \n        frame:Hide()\n        frame = nil\n    end\n    clearFrame(args._G.gnomeCanvas)\nend\n\ndata = {}\nlines = {}\nundoMark = 0\ncurrentLine = nil\nunclearLine = false\nlastPoint = nil\nleftBtn = false\nrightBtn = false\ncurrentColor = {0, 0, 0}\npenSize = 2\npenColors = {R = 0, G = 0, B = 0}\n\nbackdropInfo = {\n    bgFile = \"Interface/Buttons/WHITE8X8\",\n    edgeFile = \"Interface/DialogFrame/UI-DialogBox-Border\",\n    tile = false,\n    tileSize = 0,\n    edgeSize = 48,\n    insets = { left = 16, right = 16, top = 16, bottom = 16 }\n}\n\n--主框体\ncanvasFrame = args._G.CreateFrame(\"Frame\", \"gnomeCanvas\", args._G.UIParent,\"BackdropTemplate\")\ncanvasFrame:SetBackdrop(backdropInfo)\ncanvasFrame:SetSize(500, 500)\ncanvasFrame:SetPoint(\"CENTER\", 0, 0)\ncanvasFrame:SetFrameStrata(\"TOOLTIP\")\ncanvasFrame:SetFrameLevel(999)\ncanvasFrame:EnableMouse(true)\ncanvasFrame:SetMovable(true)\nargs._G.gnomeCanvas = canvasFrame\n\n--按下鼠标\ncanvasFrame:SetScript(\"OnMouseDown\", function(self, button)\n    if button == \"LeftButton\" or button == \"RightButton\" then\n        local x, y = args._G.GetCursorPosition()\n        local scale = self:GetEffectiveScale()\n        local left, bottom,width,height = self:GetLeft(), self:GetBottom(), self:GetWidth(), self:GetHeight()\n        local relX, relY = (x / scale) - left, (y / scale) - bottom\n        --拖动框体\n        if relX < 16 or relX > width-16 or relY < 16 or relY > height-16 then\n            self:StartMoving()\n        else\n            if button == \"LeftButton\" and rightBtn == false then\n                leftBtn = true\n                lastPoint = {x = math.floor(relX), y = math.floor(relY)}\n            elseif button == \"RightButton\" and leftBtn == false then\n                rightBtn = true\n                lastPoint = {x = math.floor(relX), y = math.floor(relY)}\n            end\n        end\n    end\nend)\n\n--释放鼠标\ncanvasFrame:SetScript(\"OnMouseUp\", function(self, button)\n    if self:IsMovable() == true then\n        self:StopMovingOrSizing()\n    end\n    if button == \"LeftButton\" and leftBtn == true then\n        currentLine = nil\n        lastPoint = nil\n        leftBtn = false\n        if undoMark == 0 then undoMark = 1 else undoMark = 0 end\n    elseif button == \"RightButton\" and rightBtn == true then\n        currentLine = nil\n        lastPoint = nil\n        rightBtn = false\n        unclearLine = true\n        if undoMark == 0 then undoMark = 1 else undoMark = 0 end\n    end\nend)\n\n--刷新和绘制\ncanvasFrame:SetScript(\"OnUpdate\", function(self, elapsed)\n    if lastPoint then\n        local x, y = args._G.GetCursorPosition()\n        local scale = self:GetEffectiveScale()\n        local left, bottom,width,height = self:GetLeft(), self:GetBottom(), self:GetWidth(), self:GetHeight()\n        local relX, relY = math.floor((x / scale) - left), math.floor((y / scale) - bottom)\n        \n        if relX >= 16 and relX <= width-16 and relY >= 16 and relY <= height-16 then\n            local dx = relX - lastPoint.x\n            local dy = relY - lastPoint.y\n            local distance = dx*dx + dy*dy\n            if distance >= penSize*penSize then\n                local line = self:CreateLine()\n                line:SetThickness(penSize)\n                line:SetColorTexture(penColors.R, penColors.G, penColors.B)\n                line:SetStartPoint(\"BOTTOMLEFT\", lastPoint.x, lastPoint.y)\n                line:SetEndPoint(\"BOTTOMLEFT\", relX, relY)\n                \n                --清理直线\n                if rightBtn == true then\n                    local line = lines[#lines]\n                    if line then\n                        if unclearLine == false then\n                            line:Hide()\n                            line:SetParent(nil)\n                            table.remove(lines)\n                            table.remove(data)\n                        else\n                            unclearLine = false\n                        end\n                    end\n                end\n                \n                --储存线段\n                table.insert(lines, line)\n                currentLine = {\n                    p1x = string.format(\"%03d\", lastPoint.x),\n                    p1y = string.format(\"%03d\", lastPoint.y),\n                    p2x = string.format(\"%03d\", relX),\n                    p2y = string.format(\"%03d\", relY),\n                    s = penSize,\n                    c1 = string.format(\"%02d\", penColors.R*100),\n                    c2 = string.format(\"%02d\", penColors.G*100),\n                    c3 = string.format(\"%02d\", penColors.B*100),\n                    u = undoMark\n                }\n                table.insert(data,currentLine)\n                if leftBtn == true then\n                    lastPoint = {x = math.floor(relX), y = math.floor(relY)}\n                end\n            end\n        end\n    end\nend)\n\n--保存数据函数\nfunction saveDraw()\n    local words = {}\n    for i, v in ipairs(data) do\n        table.insert(words, string.format(\n            \"%03d%03d%03d%03d%1d%02d%02d%02d%1d\",\n            v.p1x, v.p1y,\n            v.p2x, v.p2y,\n            v.s,\n            v.c1, v.c2, v.c3,\n            v.u\n        ))\n    end\n    return table.concat(words)\nend\n\n--读取数据函数\nfunction loadDraw(words)\n    if not words or words == \"\" then return end\n    clearLine()\n    unclearLine = true\n    for i = 1, #words, 20 do\n        local chunk = words:sub(i, i+19)\n        local p1x = tonumber(chunk:sub(1, 3)) \n        local p1y = tonumber(chunk:sub(4, 6)) \n        local p2x = tonumber(chunk:sub(7, 9)) \n        local p2y = tonumber(chunk:sub(10, 12)) \n        local s = tonumber(chunk:sub(13, 13))\n        local c1 = tonumber(chunk:sub(14, 15)) \n        local c2 = tonumber(chunk:sub(16, 17)) \n        local c3 = tonumber(chunk:sub(18, 19)) \n        local u = tonumber(chunk:sub(20, 20))\n\n        local line = canvasFrame:CreateLine()\n        line:SetThickness(s)\n        line:SetColorTexture(c1/100, c2/100, c3/100)\n        line:SetStartPoint(\"BOTTOMLEFT\", p1x, p1y)\n        line:SetEndPoint(\"BOTTOMLEFT\", p2x, p2y)\n        table.insert(lines,line)\n        local dataArgs = {\n            p1x = p1x,\n            p1y = p1y,\n            p2x = p2x,\n            p2y = p2y,\n            s = s,\n            c1 = c1,\n            c2 = c2,\n            c3 = c3,\n            u = u\n        }\n        table.insert(data,dataArgs)\n    end\nend\n\nchar = \"0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz\"\n\n--进制转换\nfunction base62(n)\n    if n == 0 then return char:sub(1, 1) end\n    local s = \"\"\n    while n > 0 do\n        local r = n % 62\n        s = char:sub(r+1, r+1) .. s\n        n = math.floor(n / 62)\n    end\n    return s\nend\n\nlocal function base62ToNum(s)\n    local n = 0\n    for i = 1, #s do\n        n = n * 62 + (char:find(s:sub(i,i),1,true) - 1)\n    end\n    return n\nend\n\n--压缩函数\nfunction compress(data)\n    if #data == 0 then return \"0\" end\n    \n    local groupSize = 8\n    local lastLen = #data % groupSize\n    if lastLen == 0 then lastLen = groupSize end\n    \n    local result = char:sub(lastLen+1, lastLen+1)\n    local maxLen = 5  -- 62⁵ > 10⁸\n\n    for i = 1, math.ceil(#data/groupSize) do\n        local numStr = data:sub((i-1)*groupSize+1, i*groupSize)\n        local num = tonumber(numStr)\n        local base62Str = base62(num)\n        base62Str = string.rep(char:sub(1,1), maxLen - #base62Str) .. base62Str\n        result = result .. base62Str\n    end\n    \n    return result\nend\n\n--解压函数\nfunction decompress(compressed)\n    if compressed == \"0\" then return \"\" end\n    \n    local firstChar = compressed:sub(1,1)\n    local lastLen = char:find(firstChar) - 1\n    local data = compressed:sub(2)\n    local groupSize = 8\n    local maxLen = 5\n    \n    local result = \"\"\n    local groupCount = #data / maxLen\n    \n    for i = 1, groupCount do\n        local base62Str = data:sub((i-1)*maxLen+1, i*maxLen)\n        local num = base62ToNum(base62Str)\n        local numStr = tostring(num)\n        \n        if i == groupCount then\n            numStr = string.rep(\"0\", lastLen - #numStr) .. numStr\n        else\n            numStr = string.rep(\"0\", groupSize - #numStr) .. numStr\n        end\n        \n        result = result .. numStr\n    end\n    \n    return result\nend\n\n--清理线条函数\nfunction clearLine()\n    for _, line in ipairs(lines) do\n        line:Hide()\n        line:SetParent(nil)\n    end\n    args._G.wipe(lines)\n    args._G.wipe(data)\nend\n\n--按钮模板\nfunction btn(temp,w,h,relat,x,y,text)\n    local btn = args._G.CreateFrame(\"Button\", nil, canvasFrame, temp)\n    btn:SetSize(w, h)\n    btn:SetPoint(relat, canvasFrame, relat, x, y)\n    if temp then btn:SetText(text) end\n    return btn\nend\n\n--进度条模板\nfunction slider(temp, relat, x, y, min, max, step, t1, t2, t3)\n    local slider = args._G.CreateFrame(\"Slider\", nil, canvasFrame, temp)\n    slider:SetSize(200, 20)\n    slider:SetPoint(relat, x, y)\n\n    slider:SetMinMaxValues(min, max)\n    slider:SetValue(min)\n\n    slider:SetObeyStepOnDrag(true)\n    slider:SetValueStep(step)\n\n    slider.Low:SetText(t1)\n    slider.High:SetText(t2)\n    slider.Text:SetText(t3)\n    slider.Text:SetPoint(\"BOTTOM\", 0, -12)    \n\n    slider:Hide()\n\n    return slider\nend\n\n--进度条背景模板\nfunction sliderBg(x, w, h)\n    local sliderBg = x:CreateTexture(nil, \"BACKGROUND\")\n    sliderBg:SetSize(w, h)\n    sliderBg:SetPoint(\"CENTER\")\n    sliderBg:SetColorTexture(0.1, 0.1, 0.1)\n    return sliderBg\nend\n\n--版头\ntitle = canvasFrame:CreateFontString(nil, \"OVERLAY\", \"GameFontHighlight\")\ntitle:SetPoint(\"TOP\", canvasFrame, \"TOP\", 0, 5)\ntitle:SetText(\"诺莫瑞根创作绘板\")\n\n--撤回按钮\nundoBtn = btn(\"UIPanelButtonTemplate\", 80, 30, \"BOTTOMRIGHT\", -150, -28, \"上一步\")\nundoBtn:SetScript(\"OnClick\", function()\n    if #data > 1 then\n        local undo = data[#data].u\n        local count = 0\n        for i = #data, 1, -1 do\n            if data[i].u == undo then\n                count = count + 1\n            else\n                break\n            end\n        end\n\n        for i = #data - count + 1, #data do\n            lines[i]:Hide()\n            lines[i]:SetParent(nil)\n            lines[i] = nil\n            data[i] = nil\n        end\n        if #data > 0 then\n            if data[#data].u == 0 then undoMark = 1 else undoMark = 0 end\n        else\n            undoMark = 0\n        end\n    elseif #data == 1 then \n        lines[1]:Hide()\n        lines[1]:SetParent(nil)\n        args._G.wipe(lines)\n        args._G.wipe(data)\n        undoMark = 0\n    else\n        effect(\"sound_id_self\", args, \"SFX\", \"47355\",false)\n    end\nend)\n\n\n--清除按钮\nclearBtn = btn(\"UIPanelButtonTemplate\", 100, 30, \"BOTTOMRIGHT\", -3, -28, \"清除画布\")\nclearBtn:SetScript(\"OnClick\", function()\n    effect(\"sound_id_self\", args, \"SFX\", \"88\",false)\n    clearLine()\nend)\n\n--关闭按钮\ncloseBtn = btn(\"UIPanelCloseButton\", 30, 30, \"TOPRIGHT\", 5, 5)\ncloseBtn:SetFrameStrata(\"TOOLTIP\")\ncloseBtn:SetFrameLevel(1000)\ncloseBtn:SetScript(\"OnClick\", function()\n    effect(\"sound_id_self\", args, \"SFX\", \"854\",false)\n    clearLine()\n    canvasFrame:Hide()\nend)\n\n--保存按钮\nsaveBtn = btn(\"UIPanelButtonTemplate\", 50, 30, \"TOPLEFT\", 5, 15, \"保存\")\nsaveBtn:SetFrameLevel(1000)\nsaveBtn:SetScript(\"OnClick\", function()\n    effect(\"sound_id_self\", args, \"SFX\", \"854\",false)\n    local saveData=saveDraw()\n    saveData = compress(saveData)\n    setVar(args,\"o\",\"saveData\",saveData)\n    local time = args._G.date(\"*t\")\n    local name = args._G.UnitName(\"player\")\n    local timeStamp = name..\"：\"..string.format(\"%02d日%02d点%02d分\", time.day, time.hour, time.min)\n    setVar(args,\"o\",\"timeStamp\",timeStamp)\nend)    \n\n--读取按钮\nloadBtn = btn(\"UIPanelButtonTemplate\", 50, 30, \"TOPLEFT\", 60, 15, \"读取\")\nloadBtn:SetFrameLevel(1000)\nloadBtn:SetScript(\"OnClick\", function()\n    effect(\"sound_id_self\", args, \"SFX\", \"854\",false)\n    local saveData =getVar(args,\"o\",\"saveData\")\n    if saveData ~= \"\" then\n        saveData = decompress(saveData)\n        loadDraw(saveData)\n        if #data > 0 then\n            undoMark = data[#data].u == 0 and 1 or 0\n        else\n            undoMark = 0\n        end\n    end\nend)\n\n--宽度\npenSizeBtn = btn(\"UIPanelButtonTemplate\", 100, 30, \"BOTTOMLEFT\", 3, -28, \"调整笔刷\")\npenSizeBtn:SetScript(\"OnClick\", function()\n    effect(\"sound_id_self\", args, \"SFX\", \"854\",false)\n    if penSizeSlider:IsVisible() then\n        penSizeSlider:Hide()\n    else\n        penSizeSlider:Show()\n        penColorsSliderR:Hide()\n        penColorsSliderG:Hide()\n        penColorsSliderB:Hide()\n        colorPreview:Hide()\n    end\nend)\n\npenSizeSlider = slider(\"OptionsSliderTemplate\", \"BOTTOMLEFT\", 0, -50, 2, 6, 1, \"细\", \"粗\", \"笔刷宽度：2\")\npenSizeSliderBg = sliderBg(penSizeSlider, 193 ,13)\npenSizeSlider:SetScript(\"OnValueChanged\", function(self, value)\n    penSizeSlider.Text:SetText(\"笔刷宽度：\"..value)\n    penSize = value\nend)    \n\n--颜色\npenColorsBtn = btn(\"UIPanelButtonTemplate\", 100, 30, \"BOTTOMLEFT\", 105, -28, \"调整颜色\")\npenColorsBtn:SetScript(\"OnClick\", function()\n    effect(\"sound_id_self\", args, \"SFX\", \"854\",false)\n    if penColorsSliderR:IsVisible() then\n        penColorsSliderR:Hide()\n        penColorsSliderG:Hide()\n        penColorsSliderB:Hide()\n        colorPreview:Hide()\n    else\n        penColorsSliderR:Show()\n        penColorsSliderG:Show()\n        penColorsSliderB:Show()\n        colorPreview:Show()\n        penSizeSlider:Hide()\n    end\nend)\n\ncolorPreview = penColorsBtn:CreateTexture(nil, \"OVERLAY\")\ncolorPreview:SetSize(40, 40)\ncolorPreview:SetPoint(\"BOTTOMLEFT\", 120, -50)\ncolorPreview:Hide()\ncolorPreview:SetColorTexture(penColors.R, penColors.G, penColors.B)\n\npenColorsSliderR = slider(\"OptionsSliderTemplate\", \"BOTTOMLEFT\", 0, -50, 1, 100, 1, \"减\", \"加\", \"红色：0\")\npenColorsSliderG = slider(\"OptionsSliderTemplate\", \"BOTTOMLEFT\", 0, -80, 1, 100, 1, \"减\", \"加\", \"绿色：0\")\npenColorsSliderB = slider(\"OptionsSliderTemplate\", \"BOTTOMLEFT\", 0, -110, 1, 100, 1, \"减\", \"加\", \"蓝色：0\")\npenColorsSliderRBg = sliderBg(penColorsSliderR, 193, 13)\npenColorsSliderGBg = sliderBg(penColorsSliderG, 193, 13)\npenColorsSliderBBg = sliderBg(penColorsSliderB, 193, 13)\npenColorsSliderR:SetScript(\"OnValueChanged\", function(self, value)\n    penColorsSliderR.Text:SetText(\"红色：\"..value)\n    if value == 100 then value = 99 end\n    penColors.R = value/100\n    colorPreview:SetColorTexture(penColors.R, penColors.G, penColors.B)\nend)\npenColorsSliderG:SetScript(\"OnValueChanged\", function(self, value)\n    penColorsSliderG.Text:SetText(\"绿色：\"..value)\n    if value == 100 then value = 99 end\n    penColors.G = value/100\n    colorPreview:SetColorTexture(penColors.R, penColors.G, penColors.B)\nend)\npenColorsSliderB:SetScript(\"OnValueChanged\", function(self, value)\n    penColorsSliderB.Text:SetText(\"蓝色：\"..value)\n    if value == 100 then value = 99 end\n    penColors.B = value/100\n    colorPreview:SetColorTexture(penColors.R, penColors.G, penColors.B)\nend)",
